import {
  BaseQueryFn,
  createApi,
  fetchBaseQuery
} from "@reduxjs/toolkit/query/react";

interface Result<T> {
  total: number;
  list: T[];
}

export interface Song {
  id: string;
  name: string;
}

const mockedBaseQuery: BaseQueryFn = (
  { url, method, data },
  { signal, dispatch, getState },
  extraOptions
) => {
  console.log({ url, method, data });
  if (!url) return Promise.resolve({ data: null });
  const [offset, limit] = url.split("&");
  //let matched = url.match(/\/v1\/songs\?offset=(\d*)&limit=(\d*)/);
};

export const songsApi = createApi({
  reducerPath: "songsApi",
  baseQuery:
    1 === 1
      ? mockedBaseQuery
      : fetchBaseQuery({ baseUrl: "http://avagap.test/api" }),
  endpoints: (builder) => ({
    getSongs: builder.query<Result<Song>, { offset: number; limit: number }>({
      //query: ({ offset, limit }) => `/v1/songs?offset=${offset}&limit=${limit}`
      query: ({ offset, limit }) => `${offset}&${limit}`,
      queryFn: ({ offset, limit }) => {
        let list: Song[] = [];
        for (let i = +offset; i < +offset + limit; i++) {
          list.push({ id: i.toString(), name: `Song ${i}` });
        }
        console.log({ list });
        return {
          data: {
            total: 100,
            list
          } as Result<Song>
        };
      }
      /*
      query: ({ offset, limit }) => ({
        url: `/v1/Artist/5e5023646582e1286/tracks?maxSize=${limit}&offset=${offset}`,
        headers: [["X-Api-Key", "4872bafc54269b2a7133705ab32c7e69"]]
      })
      */
    })
  })
});

export const { useGetSongsQuery } = songsApi;
